import axios from 'axios'

class Post extends React.Component {

    state = {
        post : [],
        isLoaded : false
    }

    componentDidMount() {
        axios.get('http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts')
        .then(res => this.setState({
            post : res.data ,
            isLoaded : true
        }))
        .catch(err => console.log(err));
    }
    
    render() {

        const isLoaded = this.state.isLoaded;
        const post = this.state.post;

        const listItems = post.map((list) =>
           <li>{list}</li>
        );

        if(isLoaded) {

            return (
                <div>
                 <ul>
                    {listItems}
                 </ul>
                </div>
            );

        }

      
    }

}

export default Post;
