import axios from 'axios';
import Router from 'next/router'
import Link from 'next/link';

class EditPost extends React.Component {

        constructor(props) {
            super(props)
            this.state = {
                post : [],
                title : '',
                content : '', 
            }
        }

        componentDidMount() {

            function getUrlParameter(name) {
                name = name.replace(/[\[]/, '\\[').replace(/[\]]/, '\\]');
                var regex = new RegExp('[\\?&]' + name + '=([^&#]*)');
                var results = regex.exec(location.search);
                return results === null ? '' : decodeURIComponent(results[1].replace(/\+/g, ' '));
            };
    
            const $id = getUrlParameter('id');
          
            axios.get('http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts/' + $id)
            .then(res => this.setState({
                post : res.data ,
                isLoaded : true
            }))
            .catch(err => console.log(err));

        }


        onClickSubmit = () => {
 
            var myHeaders = new Headers();

            myHeaders.append("Content-Type", "application/json");
            myHeaders.append("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOlwvXC9sb2NhbGhvc3Q6ODA4MFwvcmVhY3Rwb3Jmb2xpb3dwIiwiaWF0IjoxNjAxMTI1Njk5LCJuYmYiOjE2MDExMjU2OTksImV4cCI6MTYwMTczMDQ5OSwiZGF0YSI6eyJ1c2VyIjp7ImlkIjoiMSJ9fX0.6OHir4XUXO4i7cXSAeQABd1amCeoYbHDIq6JjKX1ajI");

            var raw = JSON.stringify({"title":this.state.title,"content":this.state.content,"status":"publish"});

            var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
            };

    
            fetch("http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts/", requestOptions).then((resp) => resp.json()).then(function(data) {
 
              alert("Added");
              Router.push('/dashboard');
        
            })
            .catch(function(error) {
                console.log(error);
            });


        }


        onChangeTitle = (event) => {

            this.setState({
                title : event.target.value
            })

        }

        onChangeContent = (event) => {

            this.setState({
                content : event.target.value
            })

        }


    render() {

             const isLoaded = this.state.isLoaded;
             const post = this.state.post;

             this.state.title = post.title.rendered;

            if(isLoaded) {

            return (
                <div>
                   
                    <span className="btn-danger"><Link  href="/dashboard">Return</Link></span>
              
                    <br/>
                    <div className="form-group">
                        <label>Title</label>
                        <br/>
                        <input class="form-control" type="text" value={this.state.title} onChange={this.onChangeTitle}  />
                        <br/>
                    </div>
                    <div className="form-group">
                        <label>Content</label>
                        <br/>
                        <textarea className="form-control" rows="6"  onChange={this.onChangeContent}>{post.content.rendered}</textarea>
                        <br/>
                    </div>
                    <div className="form-group">
                    <button class="btn btn-primary" onClick={this.onClickSubmit}>Edit Post </button> 
                    </div>
                </div>
            );

            } else {

                return (
                    <div></div>
                );

            }
            
      
    }

}

export default EditPost;
