import Layout from '../components/Layout';
import Single from '../components/Single';
import axios from 'axios';

const Post = async () => {
    const response = await axios.get(`http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts`);
  
    if (!response.data.length) {
      return {
        statusCode: 404,
      };
    }
  
    return {
      statusCode: 200,
      post: response.data[0],
    };
  };



export default Post;
