class Login extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            username : '',
            password : '',  
        }
    }


    onClickSubmit = () => {

       
        var data = JSON.stringify({"username":"wpadmin","password":"Clone55!"});

        var xhr = new XMLHttpRequest();
        xhr.withCredentials = true;

        xhr.addEventListener("readystatechange", function() {
        if(this.readyState === 4) {
            console.log(this.responseText);
        }
        });

        xhr.open("POST", "http://localhost:8080/reactporfoliowp/wp-json/jwt-auth/v1/token");
        xhr.setRequestHeader("Content-Type", "application/json");
        xhr.setRequestHeader("", "");

        xhr.send(data);

        

  

    }

    onChangeUsername = (event) => {
        this.setState({
            username : event.target.value
        })
    }

    onChangePassword = (event) => {

        this.setState({
            password : event.target.value
        })

    }
  

    render() {
      

            return (
                <div>
                    
                        <label>Username</label>
                        <br/>
                        <input type="text" value={this.state.username} onChange={this.onChangeUsername}  />
                        <br/>
                        <label>Password</label>
                        <br/>
                        <input type="text" value={this.state.password}  onChange={this.onChangePassword} />
                        <br/>
                        <button onClick={this.onClickSubmit} >Login </button>
                  
                </div>
            );

        
      
    }

}

export default Login;
