import Layout from '../components/Layout';
import Single from '../components/Single';
import axios from 'axios';

const Post = async (slug) =>  {

  const response = await axios.get(`http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts?slug=${slug}`)

   return  (
    <Layout>
        <div>

            <Single />

        </div>
    </Layout>

    );
};




export default Post;
