import axios from 'axios';
import Router from 'next/router'

class Post extends React.Component {

        constructor(props) {
            super(props)
            this.state = {
                post : [],
                title : '',
                content : '',  
            }
        }

        componentDidMount() {
            axios.get('http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts')
            .then(res => this.setState({
                post : res.data ,
                isLoaded : true
            }))
            .catch(err => console.log(err));
        }


        onClickSubmit = () => {

         
            var myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");
            myHeaders.append("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOlwvXC9sb2NhbGhvc3Q6ODA4MFwvcmVhY3Rwb3Jmb2xpb3dwIiwiaWF0IjoxNjAxMTI1Njk5LCJuYmYiOjE2MDExMjU2OTksImV4cCI6MTYwMTczMDQ5OSwiZGF0YSI6eyJ1c2VyIjp7ImlkIjoiMSJ9fX0.6OHir4XUXO4i7cXSAeQABd1amCeoYbHDIq6JjKX1ajI");

            var raw = JSON.stringify({"title":this.state.title,"content":this.state.content,"status":"publish"});

            var requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: raw,
            redirect: 'follow'
            };

    

            fetch("http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts/", requestOptions).then((resp) => resp.json()).then(function(data) {

           
          
            alert("added");

        

            })
            .catch(function(error) {
                console.log(error);
            });

            this.setState({
                post : post
            });

            this.refs.myForm.reset();
            this.refs.title.focus();
          
        }



        onChangeTitle = (event) => {
            this.setState({
                title : event.target.value
            })
        }

        onChangeContent = (event) => {

            this.setState({
                content : event.target.value
            })

        }


    render() {

            const isLoaded = this.state.isLoaded;
            const post = this.state.post;
      
            if(isLoaded) {

            const listItems = post.map((postlist) =>
            
            <tr>
                <td>{postlist.id}</td>
                <td>{postlist.title.rendered}</td>
                <td><button class="btn btn-primary">Edit</button></td>
                <td><button class="btn btn-danger">Delete</button></td>
            </tr>
            );
                

          

            return (
                <div>
                    <br/>
                    <h2>Show Post List</h2>
                    <div className="table-responsive">
                    <table className="table">
                        <tr>
                            <th>Id</th>
                            <th>Title</th>
                            <th>Delete</th>
                            <th>Edit</th>
                        </tr>
                        {listItems}
                    </table>
                    </div>
                    <br/><br/>
                    <h2>Add Post</h2>
                    <br/>
                    <form ref="myForm">
                        <div className="form-group">
                            <label>Title</label>
                            <br/>
                            <input ref="title" class="form-control" type="text" value={this.state.title} onChange={this.onChangeTitle}  />
                            <br/>
                        </div>
                        <div className="form-group">
                            <label>Content</label>
                            <br/>
                            <textarea ref="content" className="form-control" rows="6"  onChange={this.onChangeContent}>{this.state.content}</textarea>
                            <br/>
                        </div>
                        <div className="form-group">
                        <button class="btn btn-primary" onClick={this.onClickSubmit} >Add Post </button> 
                        </div>
                    </form>
                </div>
            );

            } else {
                return (<div></div>)
            }
        
      
    }

}

export default Post;
