class Post extends React.Component {

        constructor(props) {
            super(props)
            this.state = {
                title : '',
                content : '',  
            }
        }


        onClickSubmit = () => {

            alert("test");

        }

        onChangeTitle = (event) => {
            this.setState({
                title : event.target.value
            })
        }

        onChangeContent = (event) => {

            this.setState({
                content : event.target.value
            })

        }


    render() {
      
            return (
                <div><br/>
                    <h2>Add Post</h2>
                    <div className="form-group">
                        <label>Title</label>
                        <br/>
                        <input class="form-control" type="text" value={this.state.title} onChange={this.onChangeTitle}  />
                        <br/>
                    </div>
                    <div className="form-group">
                        <label>Content</label>
                        <br/>
                        <textarea className="form-control" rows="3"  onChange={this.onChangeContent}>{this.state.content}</textarea>
                        <br/>
                    </div>
                    <div className="form-group">
                    <button class="btn btn-primary" onClick={this.onClickSubmit} >Add Post </button> 
                    </div>
                </div>
            );

        
      
    }

}

export default Post;
