class Login extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            username : '',
            password : '',  
        }
    }


    onClickSubmit = () => {

       
    var myHeaders = new Headers();
    myHeaders.append("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOlwvXC9sb2NhbGhvc3Q6ODA4MFwvcmVhY3Rwb3Jmb2xpb3dwIiwiaWF0IjoxNjAxMTI1Njk5LCJuYmYiOjE2MDExMjU2OTksImV4cCI6MTYwMTczMDQ5OSwiZGF0YSI6eyJ1c2VyIjp7ImlkIjoiMSJ9fX0.6OHir4XUXO4i7cXSAeQABd1amCeoYbHDIq6JjKX1ajI");

    var formdata = new FormData();
    formdata.append("username", this.state.username);
    formdata.append("password", this.state.password);

    var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: formdata,
        redirect: 'follow'
    };

    fetch("http://localhost:8080/reactporfoliowp/wp-json/admin/loggedinuser", requestOptions).then(response => response.text()).then(function() {
        // Your code for handling the data you get from the API
    }).catch(error => console.log('error', error));


    fetch("http://localhost:8080/reactporfoliowp/wp-json/admin/loggedinuser", requestOptions)
    .then((resp) => resp.json())
    .then(function(data) {
    let authors = data.results;
    return authors.map(function(author) {
        let li = createNode('li'),
            img = createNode('img'),
            span = createNode('span');
        img.src = author.picture.medium;
        span.innerHTML = `${author.name.first} ${author.name.last}`;
        append(li, img);
        append(li, span);
        append(ul, li);
    })
    })
    .catch(function(error) {
    console.log(error);
    });


    }

    onChangeUsername = (event) => {
        this.setState({
            username : event.target.value
        })
    }

    onChangePassword = (event) => {

        this.setState({
            password : event.target.value
        })

    }
  

    render() {
      

            return (
                <div>
                    
                        <label>Username</label>
                        <br/>
                        <input type="text" value={this.state.username} onChange={this.onChangeUsername}  />
                        <br/>
                        <label>Password</label>
                        <br/>
                        <input type="text" value={this.state.password}  onChange={this.onChangePassword} />
                        <br/>
                        <button onClick={this.onClickSubmit} >Login </button>
                  
                </div>
            );

        
      
    }

}

export default Login;
