class Login extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            username : '',
            password : '',  
        }
    }


    onClickSubmit = () => {

       
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        myHeaders.append("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOlwvXC9sb2NhbGhvc3Q6ODA4MFwvcmVhY3Rwb3Jmb2xpb3dwIiwiaWF0IjoxNjAwMDkxNTA2LCJuYmYiOjE2MDAwOTE1MDYsImV4cCI6MTYwMDY5NjMwNiwiZGF0YSI6eyJ1c2VyIjp7ImlkIjoiMSJ9fX0.VUCI8eUZIBk443WioxzouaW4sbiWsFMDhfEmu_kwod4");
        var raw = JSON.stringify({"username":"wpadmin","password":"Clone55!"});

        var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
        };

        fetch("http://localhost:8080/reactporfoliowp/wp-json/jwt-auth/v1/token", requestOptions)
        .then(response => response.text())
        .then(result => console.log(result))
        .catch(error => console.log('error', error));


        alert(result);

  

    }

    onChangeUsername = (event) => {
        this.setState({
            username : event.target.value
        })
    }

    onChangePassword = (event) => {

        this.setState({
            password : event.target.value
        })

    }
  

    render() {
      

            return (
                <div>
                    
                        <label>Username</label>
                        <br/>
                        <input type="text" value={this.state.username} onChange={this.onChangeUsername}  />
                        <br/>
                        <label>Password</label>
                        <br/>
                        <input type="text" value={this.state.password}  onChange={this.onChangePassword} />
                        <br/>
                        <button onClick={this.onClickSubmit} >Login </button>
                  
                </div>
            );

        
      
    }

}

export default Login;
