import axios from 'axios'

class Form extends React.Component {

    constructor(props) {

        super(props)

        this.state = {
            title : '',
            content : '',
            status : 'publish'
        }

    }

  

    onClickSubmit = (event) => {

        var myHeaders = new Headers();

        myHeaders.append("Content-Type", "application/json");

        myHeaders.append("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJodHRwOlwvXC9sb2NhbGhvc3Q6ODA4MFwvcmVhY3Rwb3Jmb2xpb3dwIiwiaWF0IjoxNjAwMDkxNTA2LCJuYmYiOjE2MDAwOTE1MDYsImV4cCI6MTYwMDY5NjMwNiwiZGF0YSI6eyJ1c2VyIjp7ImlkIjoiMSJ9fX0.VUCI8eUZIBk443WioxzouaW4sbiWsFMDhfEmu_kwod4");

        var raw = JSON.stringify({"title":this.state.title,"content":this.state.content,"status":this.state.status});

        var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
        };

        fetch("http://localhost:8080/reactporfoliowp/wp-json/wp/v2/posts/", requestOptions)
        .then(response => response.text())
        .then(result => console.log(result))
        .catch(error => console.log('error', error));

        alert(result + error);

    }


    render() {
      

            return (
                <div>

                    <form>
                        <label>Username</label>

                        <input type="text" value={this.state.title}  />

                        <input type="submit" value="submit" name="submit" onClick={this.onClickSubmit} />

                    </form>
                
                </div>
            );

        
      
    }

}

export default Form;
