class Post extends React.Component {

        constructor(props) {
            super(props)
            this.state = {
                title : '',
                content : '',  
            }
        }


        onClickSubmit = () => {

            alert("test");

        }

        onChangeTitle = (event) => {
            this.setState({
                title : event.target.value
            })
        }

        onChangeContent = (event) => {

            this.setState({
                content : event.target.value
            })

        }


    render() {
      
            return (
                <div>
                    
                    <div className="form-group">
                    <label>Title</label>
                     <br/>
                        <input type="text" value={this.state.title} onChange={this.onChangeTitle}  />
                        <br/>
                    </div>
                        <label>Content</label>
                    
                    <textarea onChange={this.onChangeContent}>{this.state.content}</textarea>
                    <br/>
                    <button onClick={this.onClickSubmit} >Add Post </button> 
                </div>
            );

        
      
    }

}

export default Post;
