import axios from 'axios'

class Form extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            username : ''
        }

    }

    onChangeUsername = (event) => {

        this.setState({
            username: event.target.value
        })

        alert(username);
        
    }


    render() {
      

            return (
                <div>

                    <form>
                        <label>Username</label>
                        <input type="text" value={this.state.username} onChange={this.onChangeUsername} />

                    </form>
                
                </div>
            );

        
      
    }

}

export default Form;
